      <Carousel children={<ProjectCard />}>
        <ProjectCard
          project={prevProject}
          classes="prev"
          key={prevProject.name}
        />
        <ProjectCard project={project} active={true} key={project.name} />
        <ProjectCard
          project={nextProject}
          classes="next"
          key={nextProject.name}
        />
      </Carousel>
    </section>
  );
}

export default ProjectCarousel;
