import { useState } from "react";
export function Carousel({ children, array }) {
  const [activeIndex, setActiveIndex] = useState(0);
  const activeImg = array[activeIndex];
  const nextImg = array[getNextIndex()];
  const prevImg = array[getPrevIndex()];

  function getPrevIndex() {
    return activeIndex === 0 ? array.length - 1 : activeIndex - 1;
  }

  function getNextIndex() {
    return activeIndex === children.imgArray.length - 1 ? 0 : activeIndex + 1;
  }

  function previous() {
    setActiveIndex(getPrevIndex());
  }
  //   function next() {
  //     setActiveIndex(getNextIndex());
  //   }
  console.log(children);
  return (
    <section className="ProjectCarousel">
      <button className="prev" type="button" onClick={() => previous()}>
        &lt;
      </button>
      <span classes="prev">{children.props.children[prevImg]}</span>
      <span active={true}>{children.props.children[activeImg]}</span>
      <span classes="next">{children.props.children[nextImg]}</span>

      {/* <button className="next" type="button" onClick={() => next()}>
        &gt;
      </button> */}
    </section>
  );
}

export default Carousel;
