import React, { useState } from "react";

const CarouselContext = React.createContext();

function CarouselProvider(items){
    const [activeIndex, setActiveIndex] = useState(0);
    function getPrevIndex() {
      return activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    }

    function getNextIndex() {
      return activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    }
    function setPreviousItem() {
      setActiveIndex(getPrevIndex());
    }
    function setNextItem() {
      setActiveIndex(getNextIndex());
    }
    function getCurrentItem() {
      return items[activeIndex];
    }
    function getNextItem() {
      return items[getNextIndex()];
    }
    function getPreviousItem() {
      return items[getPrevIndex()];
    }

}

export function useCarousel(items) {
  const [activeIndex, setActiveIndex] = useState(0);
  function getPrevIndex() {
    return activeIndex === 0 ? items.length - 1 : activeIndex - 1;
  }

  function getNextIndex() {
    return activeIndex === items.length - 1 ? 0 : activeIndex + 1;
  }
  function setPreviousItem() {
    setActiveIndex(getPrevIndex());
  }
  function setNextItem() {
    setActiveIndex(getNextIndex());
  }
  function getCurrentItem() {
    return items[activeIndex];
  }
  function getNextItem() {
    return items[getNextIndex()];
  }
  function getPreviousItem() {
    return items[getPrevIndex()];
  }

  return {
    activeIndex,
    getCurrentItem,
    getPreviousItem,
    getNextItem,
    setPreviousItem,
    setNextItem,
  };
}
export function Carousel({ children, items }) {
  return (
      <CarouselProvider>
    <section className="ProjectCarousel">
      <button className="prev" type="button" onClick={() => setPreviousItem()}>
        &lt;
      </button>
      {children}
      <button className="next" type="button" onClick={() => setNextItem()}>
        &gt;
      </button>
    </section>

      </CarouselProvider>
  );
}

export default Carousel;
