import "./App.scss";
import todoAppScreenshot from "../../assets/todo-app-thumb.png";

const data = {
  projects: [
    {
      name: "Todo-App",
      thumb: todoAppScreenshot,
      alt: "Todo app screenshot",
      desc: "List and track the status of your tasks.",
    },
    {
      name: "Todo-App2",
      thumb: todoAppScreenshot,
      alt: "Todo app screenshot",
      desc: "List and track the status of your tasks.",
    },
    {
      name: "Todo-App3",
      thumb: todoAppScreenshot,
      alt: "Todo app screenshot",
      desc: "List and track the status of your tasks.",
    },
    {
      name: "Todo-App4",
      thumb: todoAppScreenshot,
      alt: "Todo app screenshot",
      desc: "List and track the status of your tasks.",
    },
  ],
};

const navItems = [
  {
    name: "Home",
    submenu: [
      {
        name: "About Me",
        href: "#",
        submenu: [
          {
            name: "Home",
            submenu: [
              {
                name: "About Me",
                href: "#",
              },
              {
                name: "Projects",
                href: "#",
              },
            ],
          },
          {
            name: "To-Do App",
            submenu: [
              {
                name: "About Me",
                href: "#",
              },
              {
                name: "Projects",
                href: "#",
              },
            ],
          },
          {
            name: "E-commerce Site",
            submenu: [
              {
                name: "About Me",
                href: "#",
              },
              {
                name: "Projects",
                href: "#",
              },
            ],
          },
        ],
      },
      {
        name: "Projects",
        href: "#",
      },
    ],
  },
  {
    name: "To-Do App",
    submenu: [
      {
        name: "About Me",
        href: "#",
      },
      {
        name: "Projects",
        href: "#",
      },
    ],
  },
  {
    name: "E-commerce Site",
    submenu: [
      {
        name: "About Me",
        href: "#",
      },
      {
        name: "Projects",
        href: "#",
      },
    ],
  },
];

function ProjectCard({ name, thumb, alt, desc }) {
  return (
    <div className="ProjectCard">
      <h4>{name}</h4>
      <img alt={alt} src={thumb} />
      <p>{desc}</p>
    </div>
  );
}

function Header() {
  return (
    <header>
      <h1>Portfolio Site</h1>
      <h4>This is my portfolio site</h4>
    </header>
  );
}

function Navbar({ navItems }) {
  return (
    <nav className="Navbar">
      {navItems.map((navItem) => (
        <NavItem navItem={navItem} key={navItem.name} />
      ))}
    </nav>
  );
}

function NavItem({ navItem }) {
  return (
    <div className="NavItem">
      <div>
        {navItem.name}
        {console.log(typeof navItem.submenu)}
        {/* {navItem.submenu.length
          ? navItem.submenu.map((submenu) => <Navbar navItems={submenu} />)
          : null} */}
      </div>
    </div>
  );
}

// function RecursiveNavbar({ navItems }) {
