import React, { useState } from "react";

const CarouselContext = React.createContext();


function CarouselProvider({ children, items }) {

  return (
    <CarouselContext.Provider
      value={{
        getPreviousItem,
        getNextItem,
        getCurrentItem,
        setPreviousItem,
        setNextItem,
      }}
    >
      {children}
    </CarouselContext.Provider>
  );
}

export function BtnPrev() {
  const { setPreviousItem } = useCarouselContext();
  return (
    <button className="prev" type="button" onClick={() => setPreviousItem()}>
      &lt;
    </button>
  );
}
export function BtnNext() {
  const { setNextItem } = useCarouselContext();
  return (
    <button className="next" type="button" onClick={() => setNextItem()}>
      &gt;
    </button>
  );
}
export function Carousel({ items, classes = "", ItemCard, itemProps }) {
  return (
      <section className={`Carousel ${classes}`}>
        <BtnPrev />

        <BtnNext />
      </section>
  );
}

export default Carousel;
