import { useState } from "react";
export function Carousel({ children }) {
  const [activeIndex, setActiveIndex] = useState(0);
  const img = children.imgArray[activeIndex];
  const nextImg = children.imgArray[getNextIndex()];
  const prevImg = children.imgArray[getPrevIndex()];

  function getPrevIndex() {
    return activeIndex === 0 ? children.imgArray.length - 1 : activeIndex - 1;
  }

  function getNextIndex() {
    return activeIndex === children.imgArray.length - 1 ? 0 : activeIndex + 1;
  }

  function previous() {
    setActiveIndex(getPrevIndex());
  }
  function next() {
    setActiveIndex(getNextIndex());
  }

  return (
    <section className="ProjectCarousel">
      <button className="prev" type="button" onClick={() => previous()}>
        &lt;
      </button>
      {(children) => {
        children(prevImg);
      }}
      {(children) => {
        children(img);
      }}
      {(children) => {
        children(nextImg);
      }}
      <button className="next" type="button" onClick={() => next()}>
        &gt;
      </button>
    </section>
  );
}

export default Carousel;
