import { useState } from "react";

export function useCarousel({items}){
    const [activeIndex, setActiveIndex] = useState(0);
    function getPrevIndex() {
      return activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    }

    function getNextIndex() {
      return activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    }
      function previous() {
        setActiveIndex(getPrevIndex());
      }
      function next() {
        setActiveIndex(getNextIndex());
      }
      function getNextItem(){return items[getNextIndex()]}
      function getPreviousItem(){return items[getPrevIndex()]}

    return {
        activeIndex,
        getPreviousItem,
        getNextItem,
        previous,
        next
    }
}
export function Carousel({ children, items }) {
    const {getPreviousItem, next} = useCarousel(items)
  return (
    <section className="ProjectCarousel">
      <button className="prev" type="button" onClick={() => previous()}>
        &lt;
      </button>
      {children}
      <button className="next" type="button" onClick={() => next()}>
        &gt;
      </button>
    </section>
  );
}

export default Carousel;
