export function ProjectCard({ name, thumb, alt, desc, active = false }) {
  return (
    <div className={`ProjectCard ${active ? "active" : ""}`}>
      {active && <h4>{name}</h4>}
      <img alt={alt} src={thumb} />
      {active }
      <p>{desc}</p>
    </div>
  );
}

export default ProjectCard;
