import ProjectCard from "./ProjectCard";
import { Carousel, useCarouselContext } from "../Carousel/Carousel";
import "./ProjectCarousel.scss";
export function ProjectCarousel({ projects }) {
  const { getPreviousItem, getNextItem, getCurrentItem } = useCarouselContext();
  const nextProject = getNextItem();
  const previousProject = getPreviousItem();
  const currentProject = getCurrentItem();
  return (
    <section className="ProjectCarousel">
      <Carousel items={projects}>
        <ProjectCard project={previousProject} classes="prev" />
        <ProjectCard project={currentProject} active={true} />
        <ProjectCard project={nextProject} classes="next" />
      </Carousel>
    </section>
  );
}

export default ProjectCarousel;
